import './Confirmation_Acount.css'
import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import Swal from 'sweetalert2'
import axios from "axios";

function Confirmation_Acount() {
    const navigator = useNavigate()
    const [data, setData] = useState({
        valor: 'false'
    })


    function handle(e) {
        const newdata = { ...data }
        newdata[e.target.id] = e.target.value
        setData(newdata)
    }

    const querystring = window.location.search
    const params = new URLSearchParams(querystring)



    const validate = () => {
        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");

        var raw = JSON.stringify({
            "email": params.get('email'),
        });

        const url = 'http://localhost:3000/api/user/config?'

        if (data.valor === 'true') {
            console.log('Validation Success: ' + data.valor);



            console.log(raw);
            axios.post(url, {
                valor: data.valor,
                email: params.get('email'),


            })
                .then(res => {
                    console.log(res.data)
                })
            Swal.fire(
                'Muy Bien!',
                'Gracias por confirma',
                'success'
            )
            

        }
        if (data.valor === 'false') {
            console.log('Validation error: ' + data.valor);

            axios.post(url, {
                valor: data.valor,
                email: params.get('email'),
            })
                .then(res => {
                    console.log(res.data)
                    Swal.fire(
                        'Muy Bien!',
                        'Gracias por confirma',
                        'success'
                    )
                navigator('/login')
                

                })
                .catch(err => {
                    Swal.fire(
                        'Tiempo agotado!',
                        'Tu tiempo se termino para configurar tu cuenta',
                        'error'
                    )

                })

           





        }
    }





    return (
        <div className="container w-25   rounder shadow">
            <div className="row align-items-stretch">
                <div className="col acount d-none d-lg-block col-md-5 col-lg-7 col-xl-6 rounder">

                </div>

                <div className="col p-5 rounder-end">
                    <div className="text-end">
                        
                    </div>
                    <h2 className=" text-center py-5">Bienvenido</h2>
                    <h4 className=" text-center">Click en el Boton!</h4><br/>

                    <form className="was-validated" onSubmit={validate}>
                        <div class="form-check">
                            <input class="form-check-input" type="checkbox"  id="flexCheckDefault" onChange={(e) => handle(e)} value={true}    />
                            <label class="form-check-label" for="flexCheckDefault" >
                                Activar y Aceptas los terminos.
                            </label>
                        </div><br />
                        <div class="d-grid gap-2">
                            <button onClick={()=>{validate();}} type="submit" className="btn btn-success" >Confirmar</button>
                        </div>

                    </form>
                </div>
            </div>
        </div>
    );
}

export default Confirmation_Acount;
