import React, {useState, useEffect} from 'react'
import {Link} from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Label, Input, FormGroup } from 'reactstrap';
import Swal from "sweetalert2";
import "./table.css"
import View from './Viex';

const URL = 'http://localhost:3000/api/product/view'

const Dashboard = () => {
    const navigate = useNavigate();
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [productos, setProductos] = useState([]);


    useEffect(() => {
        fetch("http://localhost:3000/api/product/view")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setProductos(result)
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, []);

    const { register, handleSubmit, formState: { errors } } = useForm();

    const onSubmit = value => {

        const myImput = document.getElementById('files');

        console.log(myImput.files[0])
        var formdata = new FormData();
        formdata.append("name", myImput.files[0], myImput.files[0].values);
        formdata.append("nameProduc", document.getElementById('name').value);
        formdata.append("description",document.getElementById('description').value);
        formdata.append("price", document.getElementById('price').value);
        formdata.append("amount",document.getElementById('stock').value);

        var requestOptions = {
          method: 'POST',
          body: formdata,
          redirect: 'follow'
        };

        fetch("http://localhost:3000/api/product/create", requestOptions)
          .then(response => response.text())
          .then(result => {
            Swal.fire(
                'El producto se agregado correctamente',
                'continua agregando productos ',
                'success'
            )
             props.reload()
          })
          .catch(error => console.log('error', error));


    }

    return (
        <div className="container-Dashboard">
            <div className='header-Dashboard'><h1>ASUS</h1></div>
            <div className='space-Dashboard'></div>
            <div className='navbar-Dashboard'>
                <ol>
                    <ul><a href='/imagePicker'>AÃ±adir producto</a></ul>
                </ol>

            </div>
            <div className='main-Dashboard'>
                {productos.map((product) => (
                    <Card
                        key={product.id}
                        id={product.id}
                        name={product.name}
                        nameProduc={product.nameProduc}
                        description ={product.description}
                        price={product.price}
                        amount={product.amount}

                    />
                ))}
            </div>
        </div>
    )
}
export default Dashboard;
