import axios from 'axios'
import React, {useState, useEffect} from 'react'
import {Link, useNavigate} from 'react-router-dom'
import styles from "./styles.module.scss";
import Swal from "sweetalert2";

function AddProduct (){
    const { register, handleSubmit, formState: { errors } } = useForm();

    const onSubmit = value => {

        const myImput = document.getElementById('files');
        
        console.log(myImput.files[0])
        var formdata = new FormData();
        formdata.append("name", myImput.files[0], myImput.files[0].values);
        formdata.append("nameProduc", document.getElementById('name').value);
        formdata.append("description",document.getElementById('description').value);
        formdata.append("price", document.getElementById('price').value);
        formdata.append("amount",document.getElementById('stock').value);
        
        var requestOptions = {
          method: 'POST',
          body: formdata,
          redirect: 'follow'
        };
        
        fetch("http://localhost:3000/api/product/create", requestOptions)
          .then(response => response.text())
          .then(result => {
            Swal.fire(
                'El producto se agregado correctamente',
                'continua agregando productos ',
                'success'
            )
             
          })
          .catch(error => console.log('error', error));

         
    }
    return (
        <div className="container-FormAddProduct">
            <form className='form-FormAddProduct' onSubmit={handleSubmit(onSubmit)}>
                <div className='img-FormAddProduct'>
                    <input name="image" type="file" id='files'
                        {...register("image", {})
                        }
                    />

                </div>
                <div className='inputs-FormAddProduct'>
                    <div className='input1-FormAddProduct'>
                        <label htmlFor='title'>Titulo:</label>
                        <input type="text" autoComplete="off" name="title" placeholder='title' id='name'
                            {...register("title", {
                                required: {
                                    value: true,
                                    message: "Necesitas este campo"
                                }
                            })}
                        />
                    </div>
                    <div className='input2-FormAddProduct'>
                        <label htmlFor='description'>Descripccion:</label>
                        <input type="text" autoComplete="off" name="description" placeholder="description" id='description'
                            {...register("description", {
                                required: {
                                    value: true,
                                    message: "Campo requerido"
                                }
                            })}
                        />
                    </div>
                    <div className='input3-FormAddProduct'>
                        <label htmlFor='price'>Precio:</label>
                        <input type="text" autoComplete="off" name="price" placeholder="price" id='price'
                            {...register("price", {
                                required: {
                                    value: true,
                                    message: "Campo requerido"
                                }
                            })}
                        />
                    </div>
                    <div className='input4-FormAddProduct'>
                        <label htmlFor='stock'>Stock:</label>
                        <input type="text" autoComplete="off" name="stock" placeholder="stock" id='stock'
                            {...register("stock", {
                                required: {
                                    value: true,
                                    message: "Campo requerido"
                                }
                            })}
                        />
                    </div>
                    <div className='button-FormAddProduct'>
                        <button type='submit'>Add</button>
                    </div>
                </div>
            </form>
            <div className='show-FormAddProduct'></div>
        </div>
    )
}
export default AddProduct;
