import { useState } from "react";

import { Link, useNavigate } from "react-router-dom";
import styles from "./styles.module.css";
import crear from "../../assets/img/cuenta.png";
import {AiOutlineEye, AiOutlineEyeInvisible} from "react-icons/ai";


const Signup = () => {
	const peticion = {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(
            {
                "firstName": Name,
                "email": email,
                "password": password,
            }
        )
    };



	var myHeaders = new Headers();
            myHeaders.append("Content-Type", "application/json");

            var raw = JSON.stringify({
                "Name": value.Name,
                "email": value.email,
                "password": value.password
            });

            var requestOptions = {
                method: 'POST',
                headers: myHeaders,
                body: raw,
                redirect: 'follow'
            };

            fetch("http://localhost:3000/api/user/login", requestOptions)
                .then(response => response.json())
                .then(result => {})
				.catch(error => console.error);


	return (
		<div className={styles.signup_container}>
			<div className={styles.signup_form_container}>
				<div className={styles.left}>


					<Link to="/login">
						<button type="button" className={styles.white_btn}>
							Return
						</button>
					</Link>
				</div>
				<div className={styles.right}>
					<form className={styles.form_container} onSubmit={handleSubmit}>
						<h1>Create Account</h1>
						<img src={crear} width="200px" alt="User Icon"/>
						<input
							type="text"
							placeholder="Name"
							name="name"
							onChange={handleChange}
							value={data.name}
							required
							className={styles.input}
						/>
						<input
							type="email"
							placeholder="Email"
							name="email"
							onChange={handleChange}
							value={data.email}
							required
							className={styles.input}
						/>
						<div className={styles.password}>
						<input
							type={state ? "text": "password" }
							placeholder="Password"
							name="password"
							onChange={handleChange}
							value={data.password}
							required
							className={styles.input}
						/> 	
							<button type="button"  className={styles.btn} onClick={toggleBtn}>
								{
									state ? <AiOutlineEyeInvisible/> : 
									<AiOutlineEye/>
								}
							</button>




						</div>


						{error && <div className={styles.error_msg}>{error}</div>}
						<button type="submit" className={styles.green_btn}>
							Sing Up
						</button>

					</form>
				</div>
			</div>
		</div>
	);
};

export default Signup;
