import axios from "axios";
import { Link, useNavigate} from "react-router-dom";
import styles from "./styles.module.css";
import React,{useState} from"react";
import {AiOutlineEye, AiOutlineEyeInvisible} from "react-icons/ai";
import log from "../../assets/img/reg.png";
import Swal from "sweetalert2";


const Login = () => {

	const [inputs, setInputs] = useState({ email: "", password: "" });
  const [mensaje, setMensaje] = useState();
  const [loading, setLoading] = useState(false);
	const navigator = useNavigate();
	const [state, setstate] = useState(false);

	const {email, password} = inputs

	const toggleBtn = () =>{
		setstate(prevState => !prevState);
	}

	const HandleChange = (e) => {
		setInputs({ ...inputs, [e.target.name]: e.target.value });
	  };

	  const onSubmit = async (e) => {
		e.preventDefault();
		if (email !== "" && password !== "") {
		  const data = {
			email ,
			password,
		  };
		  try {
			const url = "http://localhost:3000/api/user/login ";
			const { data: res } = await axios.post(url, data);
			localStorage.setItem("token", res.data);
			Swal.fire({
			  icon: 'success',
			  title: 'Excelente',
			  text: 'Login Exitoso!',
			})
			window.location = "/blog";
		} catch (error) {
			Swal.fire({
			  icon: 'error',
			  title: 'Error',
			  text: 'Usuario o Contraseña Incorrecta!',
			})
			setTimeout(() => {
			  setMensaje("");
			}, 1500);{
			  Error(error.response.data.message);
			}
			console.error(error);
		}


	};







	return (
		<div className={styles.login_container}>
			<div className={styles.login_form_container}>
				<div className={styles.left}>
					<form className={styles.form_container} onSubmit={handleSubmit}>
						<h1>Login to Your Account</h1>
						<input
							type="email"
							placeholder="Email"
							name="email"
							onChange={handleChange}
							value={inputs.email}
							required
							className={styles.input}
						/>
						<div  className={styles.password}>
						<input
							type={state ? "text": "password" }
							placeholder="Password"
							name="password"
							onChange={handleChange}
							value={inputs.password}
							required
							className={styles.input}
						/>


						<button type="button"  className={styles.btn} onClick={toggleBtn}>
							{
								state ? <AiOutlineEyeInvisible/> : 
								<AiOutlineEye/>
							}
						</button>
						</div>





						<button type="submit" className={styles.green_btn}>
							Sing In
						</button>





							<Link className="underlineHover  formFooter" to="/pass">Forgot Password?</Link>


						{mensaje && <div className={styles.toast}>{mensaje}</div>}

					</form>
				</div>
				<div className={styles.right}>
					<h1>New Here ?</h1>
					<img src={log} width="200px" alt="User Icon"/>

					<Link to="/signup">
						<button type="button" className={styles.white_btn}>
							Sing Up
						</button>
					</Link>



				</div>
			</div>

		</div>
	);
};
}
export default Login;
