import axios from "axios";
import { Link, useNavigate} from "react-router-dom";
import styles from "./styles.module.css";
import React,{useState} from"react";
import {AiOutlineEye, AiOutlineEyeInvisible} from "react-icons/ai";
import log from "../../assets/img/reg.png";
import Swal from "sweetalert2";


const Lo = () => {




    const navigate = useNavigate()
    const { register, handleSubmit, formState: { errors } } = useForm();


    const onSubmit = value => {
        const data = value;

        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");

        var raw = JSON.stringify({
            "email": value.email,
            "password": value.password
        });

        var requestOptions = {
            method: 'POST',
            headers: myHeaders,
            body: raw,
            redirect: 'follow'
        };

        fetch("http://localhost:3000/api/user/login", requestOptions)
            .then(response => response.json())
            .then(result => {
                switch (result.error) {
                    case "Usuario no encontrado":
                        Swal.fire({
                            title: 'Error!',
                            text: 'Usuario no encontrado',
                            icon: 'error',
                            confirmButtonText: 'Cool'
                        })
                        break;
                    case "Usuario no verificado":
                        Swal.fire({
                            title: 'Error!',
                            text: 'Usuario no verificado',
                            icon: 'error',
                            confirmButtonText: 'Cool'
                        })
                        break;
                    case "contraseña no válida":
                        Swal.fire({
                            title: 'Error!',
                            text: 'contraseña no válida',
                            icon: 'error',
                            confirmButtonText: 'Cool'
                        })
                        break;

                    default:
                        navigate('/dashboard');
                        break;
                }
            })
            .catch(error => console.error);

    }


    return (
        <div className="container-Login">
            <div className='title-login'><h1>Iniciar sesion</h1></div>

            <form className="form-Login" onSubmit={handleSubmit(onSubmit)}>





                <div className="input-email-Login">
                    <input type="email" autoComplete="off" name="email" placeholder="ejemplo@gmail.com"
                        {...register("email", {
                            required: {
                                value: true,
                                message: "Campo requerido"
                            },
                            pattern: {
                                value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i,
                                message: "El formato no es correcto"
                            }
                        })}
                    />
                    <div className="error-input-email">{errors.email && <p>{errors.email.message}</p>}</div>
                </div>


                <div className="input-password-Login">
                    <input type="password" name="password" placeholder="Contraseña"
                        {...register("password", {
                            required: {
                                value: true,
                                message: "El campo es requerido"
                            },
                            minLength: {
                                value: 8,
                                message: "Necesita al menos 8 caracteres"
                            },
                            pattern: {
                                value: /^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=\S+$).{6,}$/i,
                                message: "Debe de contener mayusculas, numeros y algun caracter especial "
                            }
                        })}
                    />
                    <div className="error-input-password">{errors.password && <p>{errors.password.message}</p>}</div>
                </div>


                <div className="container-button-Login">
                    <div className="button-Login">
                        <button type='submit' ><h2>iniciar sesion</h2></button>
                    </div>
                </div>

            </form>
            <div className='links-Login'>
                <div className='link-forgotpassword-login'>
                    <div className='linka-div'>¿Olvidaste tu contraseña?<Link className='linka' to={"/recover"}>Recuperar</Link></div>
                </div>
                <div className='link-forgotpassword-login'>
                    <div className='linka-div'>
                        ¿Tienes una cuenta?
                        <Link className='linka' to={"/register"}>Registrarse</Link></div>
                </div>
            </div>

        </div>
    )
}

export default FormLogin;
