import { useState } from "react";

import { Link, useNavigate } from "react-router-dom";
import styles from "./styles.module.css";
import crear from "../../assets/img/cuenta.png";
import {AiOutlineEye, AiOutlineEyeInvisible} from "react-icons/ai";


const Signup = () => {

	const [state, setstate] = useState(false);

	const toggleBtn = () =>{
		setstate(prevState => !prevState);
	}

	const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");


	const add = () => {
		var myHeaders = new Headers();
            myHeaders.append("Content-Type", "application/json");

            var raw = JSON.stringify({
                "name": name,
                "email": email,
                "password": password
            });

            var requestOptions = {
                method: 'POST',
                headers: myHeaders,
                body: raw,
                redirect: 'follow'
            };

            fetch("http://localhost:3000/api/user/login", requestOptions)
                .then(response => response.json())
                .then(result => {})
				.catch(error => console.error);

	}


	return (
		<div className={styles.signup_container}>
			<div className={styles.signup_form_container}>
				<div className={styles.left}>


					<Link to="/login">
						<button type="button" className={styles.white_btn}>
							Return
						</button>
					</Link>
				</div>
				<div className={styles.right}>
					<form className={styles.form_container} onSubmit={add}>
						<h1>Create Account</h1>
						<img src={crear} width="200px" alt="User Icon"/>
						<input
							type="text"
							placeholder="Name"
							name="name"
							onChange={(e) => setName(e.target.value)}
							required
							className={styles.input}
						/>
						<input
							type="email"
							placeholder="Email"
							name="email"
							onChange={(e) => setEmail(e.target.value)}
							required
							className={styles.input}
						/>
						<div className={styles.password}>
						<input
							type={state ? "text": "password" }
							placeholder="Password"
							name="password"
							onChange={(e) => setPassword(e.target.value)}

							required
							className={styles.input}
						/> 	
							<button type="button"  className={styles.btn} onClick={toggleBtn}>
								{
									state ? <AiOutlineEyeInvisible/> : 
									<AiOutlineEye/>
								}
							</button>




						</div>



						<button type="submit" className={styles.green_btn}>
							Sing Up
						</button>

					</form>
				</div>
			</div>
		</div>
	);
};

export default Signup;
