import React from "react";
import {useEffect, useState} from "react";
import {Link, Route, useNavigate} from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.css'


const Dashboard = () => {
    const navigate = useNavigate();
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [productos, setProductos] = useState([]);


    useEffect(() => {
        fetch("http://localhost:3000/api/product/view")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setProductos(result)
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])


        const renderProducto = () => {

            return (
            <div className="table-responsive px-md-4 px-2 pt-3">
 <tbody>
                <div>


                {

                    productos.map((producto, index) => (


                        <tr className=" border-bottom ">
                            <td>
                                <div className=" d-flex align-items-center ">
                                    <div>
                                        <img
                                            className=" pic "
                                            src="https://images.pexels.com/photos/7322083/pexels-photo-7322083.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500"
                                            alt=""
                                        />
                                    </div>
                                    <div className="ps-3 d-flex flex-column justify-content">
                                        <p className="fw-bold">
                                           #{producto.id} <span className="ps-1">{producto.nameProduc}</span>
                                        </p>
                                        <small className=" d-flex">
                          <span className=" text-muted">
                            {producto.description}
                          </span>
                                        </small>
                                    </div>
                                </div>
                            </td>
                            <td>
                                <div className="d-flex">
                                    <p className="pe-3">
                                        Price:  <span className="red">${producto.price}</span>
                                    </p>

                                </div>
                            </td>
                            <td>
                                <div className=" d-flex align-items-center ">
                                    <span className="pe-3 text-muted">Stock:</span>
                                    <span className="pe-3">{producto.amount}</span>
                                    <div className="col-sm-3">
                                        <a
                                            type="button"
                                            className="btn btn-primary btn-sm"
                                            href="/edit-product"
                                        >
                                            Edit
                                        </a>
                                    </div>
                                    <div className="col-sm-3">
                                        <a
                                            type="button"
                                            className="btn btn-danger btn-sm"
                                            href="/delete-product"
                                        >
                                            Delete
                                        </a>
                                    </div>
                                </div>
                            </td>
                        </tr>

                    ))
                }
                </div>
                </tbody>

            </div>



            );
        };

        return (
            <div className="auth-wrapper">




                <div className="card dash-inner ">
                    <div>
                        <div className="d-flex">
                            <a type="button" className="btn btn-success btn-sm ms-auto" href="/createprod">
                                Add new product
                            </a>
                        </div>
                        <div className=" table-responsive px-md-4 px-2 pt-3 justify-content-center ">
                            <table className="table table-borderless">

                                {renderProducto()}

                            </table>
                        </div>
                    </div>
                </div>
            </div>
        );
    };

export default Dashboard;
