import axios from "axios";
import { Link, useNavigate} from "react-router-dom";
import { useForm } from 'react-hook-form';
import styles from "./styles.module.css";
import React,{useState} from"react";
import {AiOutlineEye, AiOutlineEyeInvisible} from "react-icons/ai";
import log from "../../assets/img/reg.png";
import Swal from "sweetalert2";


const Login = () => {

	const [inputs, setInputs] = useState({ email: "", password: "" });
  	const [mensaje, setMensaje] = useState();
	const navigate = useNavigate();
	const [state, setstate] = useState(false);
	const { register, handleSubmit, formState: { errors } } = useForm();

	const {email, password} = inputs

	const toggleBtn = () =>{
		setstate(prevState => !prevState);
	}

	const HandleChange = (e) => {
		setInputs({ ...inputs, [e.target.name]: e.target.value });
	};

    const onSubmit = value => {
        const data = value;

        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");

        var raw = JSON.stringify({
            "email": value.email,
            "password": value.password
        });

        var requestOptions = {
            method: 'POST',
            headers: myHeaders,
            body: raw,
            redirect: 'follow'
        };

        fetch("http://localhost:3000/api/user/login", requestOptions)
            .then(response => response.json())
            .then(result => {
                switch (result.error) {
                    case "Usuario no encontrado":
                        Swal.fire({
                            title: 'Error!',
                            text: 'Usuario no encontrado',
                            icon: 'error',
                            confirmButtonText: 'Cool'
                        })
                        break;
                    case "Usuario no verificado":
                        Swal.fire({
                            title: 'Error!',
                            text: 'Usuario no verificado',
                            icon: 'error',
                            confirmButtonText: 'Cool'
                        })
                        break;
                    case "contraseña no válida":
                        Swal.fire({
                            title: 'Error!',
                            text: 'contraseña no válida',
                            icon: 'error',
                            confirmButtonText: 'Cool'
                        })
                        break;

                    default:
                        navigate('/dashboard');
                        break;
                }
            })
            .catch(error => console.error);

    }


	return (
		<div className={styles.login_container}>
			<div className={styles.login_form_container}>
				<div className={styles.left}>
					<form className={styles.form_container} onSubmit={handleSubmint(onSubmit)}>
						<h1>Login to Your Account</h1>
						<input
							type="email"
							placeholder="Email"
							name="email"
							onChange={(e) => HandleChange(e)}
							value={email}
							className={styles.input}

						/>
						<div  className={styles.password}>
						<input
							type={state ? "text": "password" }
							placeholder="Password"
							name="password"
							onChange={(e) => HandleChange(e)}
							value={password}

							className={styles.input}
						/>


						<button type="button"  className={styles.btn} onClick={toggleBtn}>
							{
								state ? <AiOutlineEyeInvisible/> : 
								<AiOutlineEye/>
							}
						</button>
						</div>

						<button type="submit" className={styles.green_btn}>
							Sing In
						</button>
							<Link className="underlineHover  formFooter" to="/pass">Forgot Password?</Link>


						{mensaje && <div className={styles.toast}>{mensaje}</div>}

					</form>
				</div>
				<div className={styles.right}>
					<h1>New Here ?</h1>
					<img src={log} width="200px" alt="User Icon"/>

					<Link to="/signup">
						<button type="button" className={styles.white_btn}>
							Sing Up
						</button>
					</Link>



				</div>
			</div>

		</div>
	);
};

export default Login;
