import axios from "axios";
import React, {useState, useEffect} from 'react'
import {Link,useNavigate,useParams} from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Label, Input, FormGroup } from 'reactstrap';
import Swal from "sweetalert2";

import "./table.css"
import View from './Viex';

const URL = 'http://localhost:3000/api/product/view'

const Dashboard = () => {
    const [modaldelet, setModaldelet] = useState(false);
    const delet = () => setModaldelet(!modaldelet);
    const [data, setData] = useState({
        id: '',

        name: '',
        nameProduc: '',
        description: '',
        price: '',
        amount: '',
        selectedFile: null
    })
    const [blogs, setBlog] = useState([])
    useEffect( ()=>{
        getBlogs()
    },[])

    function handledelet(d) {
        d.preventDefault();
        const newdata = { ...data }
        newdata[d.target.id] = d.target.value
        setData(newdata)
        console.log(newdata)

    }

    const handleFileSelected = (e) => {
        const files = Array.from(e.target.files)
        console.log("files:", files);

        if (files && files.length > 0) {
            const newdata = {...data}
            newdata["selectedFile"] = files[0];
            setData(newdata)
        }
    }



    const [title, setTitle] = useState('')
    const [description, setDescription] = useState('')
    const [price, setPrice] = useState('')
    const [amount, setAmount] = useState('')
    const navigate = useNavigate()
    const {id} = useParams()

    //procedimiento para actualizar
    const update = async (e) => {
        e.preventDefault()
        console.log(e)
        await axios.put(`http://localhost:3000/api/product/update`)
        console.log(e)
        navigate('/blog')
    }

    useEffect( ()=>{
        getBlogById()
    },[])

    const getBlogById = async () => {
        console.log(data)
        const res = await axios.get(URL+id)
        id(res.data.id)
        setTitle(res.data.title)
        setDescription(res.data.description)
        setPrice(res.data.price)
        setAmount(res.data.amount)
    }



    //procedimineto para mostrar todos los blogs
    const getBlogs = async () => {
        const res = await axios.get(URL)
        setBlog(res.data)
    }
    //procedimineto para eliminar un blog
    const urldelet = 'http://localhost:3000/api/product/delete'

    const EnviarDelet = (id,d) => {

        axios.delete(urldelet, {
            data: data,
        })
            .then(res => {
                setData(res.data)
            })
            .catch(err => {
                console.log(err)
            })
        Swal.fire({
            title: 'Datos Eliminados!',
            text: "",
            icon: 'success',
            confirmButtonColor: '#0e46ff',
            confirmButtonText: 'Okay'
        }).then((result) => {
            if (result.isConfirmed) {
               // window.location.replace('/blog');
            }

        })

        getBlogs()
    }





    return(
        <div className='container'>
            <div className='row'>
                <div className='col'>
                    <div class="btn-group" role="group" aria-label="Basic mixed styles example">

                    <button type="submit"   className="btn">
                        <Link to="/login"  className='btn btn-secondary '> {"Salir"}</Link></button>

                    </div>
                    <button type="submit" class="btn">
                        <Link to="/create"  className='btn btn-success'> {"agregar"}</Link>

                        </button>


                    <table className='table table-sm'>
                        <thead className='table-dark'>
                        <tr>
                            <th>Name</th>
                            <th>Title</th>
                            <th>Description</th>
                            <th>Price</th>
                            <th>Amount</th>
                           <th>Action</th> 
                        </tr>
                        </thead>

                        <form>
                            <Modal isOpen={modaldelet}>
                                <ModalHeader className="text-primary">Delete Product</ModalHeader>
                                <ModalBody>
                                    <form className="was-validated" noValidate>
                                        <FormGroup>
                                            <h3>Copia y Pega el Token</h3>
                                            <div className="alert alert-primary" role="alert">
                                                <View/>
                                            </div>
                                            <div>
                                                <Label for="price">Token</Label>
                                                <input type="text" className="form-control"
                                                       onChange={(d) => handledelet(d)} id="id" value={data.id}
                                                       placeholder="Token" required></input>
                                            </div>
                                        </FormGroup>
                                    </form>
                                </ModalBody>
                                <ModalFooter>
                                    <Button type="submit" onClick={EnviarDelet} color="primary">Guardar</Button>
                                    <Button color="secondary" onClick={delet}>Cancel</Button>
                                </ModalFooter>
                            </Modal>
                        </form>
                        <tbody>
                        { blogs.map ( (blog) => (
                            <tr key={ blog.id}>
                                <td> <img src={blog.name} className="img-fluid" alt="..."></img></td>
                                <td> { blog.nameProduc } </td>
                                <td> { blog.description } </td>
                                <td> { blog.price } </td>
                                <td> { blog.amount } </td>
                                <td>
                                <button onClick={delet} className="btn btn-danger" type="submit">

                                Delete
                            </button>
                      <pre/>






                            <button type="button" class="btn btn-warning">Editar</button>



                                </td>




                            </tr>
                        )) }
                        </tbody>
                    </table>











                </div>
            </div>


        </div>




    )
}
export default Dashboard;
