import React from 'react';

import { useForm } from 'react-hook-form';

import Box from '@mui/material/Box';

import Modal from '@mui/material/Modal';


const Card = (props) => {
    const style = {
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: 400,
        height: 600,
        bgcolor: 'background.paper',
        border: '2px solid #000',
        boxShadow: 24,
        p: 4,
    };
    const [open, setOpen] = React.useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);




    const { register, handleSubmit, formState: { errors } } = useForm();
   


    const eliminar = () => {
        console.log("Entre")
        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");

        var raw = JSON.stringify({
            "id": props.id
        });

        var requestOptions = {
            method: 'DELETE',
            headers: myHeaders,
            body: raw,
            redirect: 'follow'
        };

        fetch("http://3.138.198.147/api/product/delete", requestOptions)
            .then(response => response.text())
            .then(result => console.log(result))
            .catch(error => console.log('error', error));
    }

    return (
        <div className='container-Card'>
            <div className='img-Card'>
                <img src={props.name}></img>
            </div>
            <div className='description-Card'>
                <div className='title-description-Card'><h1>{props.nameProduc}</h1></div>
                <div className='text-description-Card'>{props.description}</div>

            </div>
            <div className='price-Card'>
                <h3> $ {props.price}</h3>
            </div>
            <div className='stock-Card'>
                <h3>Stock: {props.amount}</h3>
            </div>
            <div className='buttons-Card'>
                <button className='button-1-Card' onClick={handleOpen}>
                    EDITAR
                </button>
                <button className='button-2-Card' onClick={() => (eliminar())}>
                    Eliminar
                </button>
            </div>

            <div>
                <Modal
                    open={open}
                    onClose={handleClose}
                    aria-labelledby="modal-modal-title"
                    aria-describedby="modal-modal-description"
                >
                    <Box sx={style}>
                    <div className='modal-update'>
                            <div className='div-input-modal' ><input className='input-modal' placeholder='titulo' id='titulo' defaultValue={props.nameProduc}></input></div>
                            <div className='div-input-modal'><input className='input-modal' placeholder='descripccion' id='descripcion' defaultValue={props.description}></input></div>
                            <div className='div-input-modal'><input className='input-modal' type="number" placeholder='precio' id='precio' defaultValue={props.price}></input></div>
                            <div className='div-input-modal'><input className='input-modal' type="number" placeholder='stock' id='stock' defaultValue={props.amount}></input></div>
                            <div className='div-input-modal'><button className='input-modal' type='' onClick={() => {
                                var formdata = new FormData();
                                formdata.append("id", props.id);
                                console.log(props.id)
                                formdata.append("nameProduc", document.getElementById('titulo').value);
                                formdata.append("description", document.getElementById('descripcion').value);
                                formdata.append("price", document.getElementById('precio').value);
                                formdata.append("amount", document.getElementById('stock').value);



                                var requestOptions = {
                                    method: 'PUT',
                                    body: formdata,
                                    redirect: 'follow'
                                }
                                fetch("http://3.138.198.147/api/product/update", requestOptions)
                                    .then((res) => res.json)
                                    .then((data) => console.log(""))
                                    .catch((err) => console.error(err));
                                    console.log("Entre")
                                

                            }}>actualizar </button></div>
                        </div>
                    </Box>
                </Modal>
            </div>

        </div>

    )
}
