import axios from "axios";
import React, {useState, useEffect} from 'react'
import {Link} from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css'
import Swal from "sweetalert2";
import "../table.css"

const URL = 'http://localhost:3000/api/product/view'

const Dashboard = () => {

    const [blogs, setBlog] = useState([])
    useEffect( ()=>{
        getBlogs()
    },[])

    //procedimineto para mostrar todos los blogs
    const getBlogs = async () => {
        const res = await axios.get(URL)
        setBlog(res.data)
    }
    //procedimineto para eliminar un blog
    const deleteBlog = async (id,e) => {
        e.preventDefault();
        axios.delete(`http://localhost:3000/api/product/delete`)
            .then(res => console.log(res))

            .catch((err) => {
                console.log(id,e)
                Swal.fire(
                    ("Datos No Eliminados"));
                console.log(err);
            })
        getBlogs()
    }

    return(
        <div className='container'>
            <div className='row'>
                <div className='col'>
                    <button type="submit">
                        <Link to="/create"  className='btn btn-primary mt-2 mb-2'><i className="fas fa-plus"></i> {"agregar"}</Link></button>

                    <table className='table'>
                        <thead className='table-primary'>
                        <tr>
                            <th>Name</th>
                            <th>Title</th>
                            <th>Description</th>
                            <th>Price</th>
                            <th>Amount</th>
                        </tr>
                        </thead>
                        <tbody>
                        { blogs.map ( (blog) => (
                            <tr key={ blog.id}>
                                <td> <img src={blog.name} className="img-fluid" alt="..."></img></td>
                                <td> { blog.nameProduc } </td>
                                <td> { blog.description } </td>
                                <td> { blog.price } </td>
                                <td> { blog.amount } </td>
                                <td>
                                    <Link to={`/edit/${blog.id}`} className='btn btn-info'><i className="fas fa-edit"></i>{"Editar"}   </Link>
                                    <button onClick={ (e)=>deleteBlog(blog.id, e) } type="button" className='btn btn-danger'><i className="fas fa-trash-alt">{"Eliminar"}</i></button>
                                </td>
                            </tr>
                        )) }
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    )

}

export default Dashboard;
