import axios from "axios";
import { Link} from "react-router-dom";
import styles from "./styles.module.css";
import React,{useState} from"react"

import log from "../../assets/img/reg.png";

const Login = () => {
	const [data, setData] = useState({ email: "", password: "" });
	const [error,  Error] = useState("");
	const [mensaje, setMensaje] = useState();

	const handleChange = ({ currentTarget: input }) => {
		setData({ ...data, [input.name]: input.value });
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		try {
			const url = "http://localhost:3000/api/user/login ";
			const { data: res } = await axios.post(url, data);
			localStorage.setItem("token", res.data);
			//window.location = "/blog";
		  } catch (error) {
			console.error(error);
			  setMensaje("Correo u contraseña incorrecta");
			  setTimeout(() => {
				setMensaje("");
			  }, 1500);{
			  Error(error.response.data.message);
			}
		  }
	};

	return (
		<div className={styles.login_container}>
			<div className={styles.login_form_container}>
				<div className={styles.left}>
					<form className={styles.form_container} onSubmit={handleSubmit}>
						<h1>Login to Your Account</h1>
						<input
							type="email"
							placeholder="Email"
							name="email"
							onChange={handleChange}
							value={data.email}
							required
							className={styles.input}
						/>

						<input
							type="password"
							placeholder="Password"
							name="password"
							onChange={handleChange}
							value={data.password}
							required
							className={styles.input}





						/>
					      <button
						  type="button"
						  className
						  ></button>	
						{error && <div className={styles.error_msg}>{error}</div>}
						<button type="submit" className={styles.green_btn}>
							Sing In
						</button>
						<Link className="underlineHover  formFooter" to="/pass">Forgot Password?</Link>


						{mensaje && <div className={styles.toast}>{mensaje}</div>}

					</form>
				</div>
				<div className={styles.right}>
					<h1>New Here ?</h1>
					<img src={log} width="200px" alt="User Icon"/>

					<Link to="/signup">
						<button type="button" className={styles.white_btn}>
							Sing Up
						</button>
					</Link>



				</div>
			</div>

		</div>
	);
};

export default Login;
