import styles from "./styles.module.css";
import { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

import seguridad from "../../assets/img/pass.png";

const Email = () =>{
    const [data, setData] = useState({
		password: "",
	
	});
    const [valid, setValid] = useState({
		password: "",
        password2: ""
	
	});
	const [error, setError] = useState("");
	const navigate = useNavigate();

	const handleChange = ({ currentTarget: input }) => {
		setData({ ...data, [input.name]: input.value });
        setValid({...valid, [input.name]: input.value});
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		try {
			const url = "http://localhost:3000/api/user/";
			const { data: res } = await axios.post(url, data);
			navigate("/login");
			console.log(data);
		} catch (error) {
			if (
				error.response &&
				error.response.status >= 400 &&
				error.response.status <= 500
			) {
				setError(error.response.data.message);
			}
		}
	};


    const validate = () =>{
        if(valid.password === valid.password2){
            alert('Contrase単a Valida')
          handleSubmit();
        }else{
            alert('Las contrase単as no coinciden');
        }


    }

    return (
		<div className={styles.signup_container}>
			<div className={styles.signup_form_container}>
				<div className={styles.left}>
				<img src={seguridad} width="200px" alt="User Icon"/>
				
				
					<Link to="/login">
						<button type="button" className={styles.white_btn}>
							Return
						</button>
					</Link>
				</div>
				
				<div className={styles.right}>

				<h1>Confirm password </h1>

				
				
					
					<form className={styles.form_container} >
						<input
                        	type="password"
							placeholder="Password"
							name="password"
							onChange={handleChange}

							value={data.password}
							required
							className={styles.input}
						/>
                        <input
                        	type="password"
							placeholder="Password"
							name="password2"
							onChange={handleChange}
							value={data.password2}
							required
							className={styles.input}
						/>
						{error && <div className={styles.error_msg}>{error}</div>}
						<button type="submit" onClick={()=>{validate();}} className={styles.green_btn}>
							Send
						</button>
                     
					</form>
				</div>
			</div>
		</div>
	);

}

export default Email;
