import styles from "./styles.module.css";
import { useState } from "react";
import { useForm} from "react-hook-form";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

import seguridad from "../../assets/img/pass.png";
import FormRange from "react-bootstrap/esm/FormRange";
import Swal from "sweetalert2";

const Email = () =>{

const querystring = window.location.search
const params  = new URLSearchParams(querystring)

    const [data, setData] = useState({
		password: "",
	
	});

	const url = 'http://localhost:3000/api/user/login'
	const navigator = useNavigate()
	const{handleSubmit, register, formState: {errors}} = useForm();
	
	const onSubmit = values =>{
	var myHeaders = new Headers()
	myHeaders.append('Content-Type', 'application/json')

	var raw = JSON.stringify ({
		"email": params.get('email'),
	});

	if(values.password1 == values.password) {
		const data = values
		axios.put(url,{
			email: params.get('email'),
			password: data.password
		})
		.then(response => {
		console.log(response.data);
		Swal.fire(
			''+response.data.err+'!',
			'',
			'success',
		)
		})
		navigator('/login')
	}
	else {
		Swal.fire(
            'Password Error',
			'Valida que sean igual',
            'error',
        )
		}
}



    return (
		<div className={styles.signup_container}>
			<div className={styles.signup_form_container}>
				<div className={styles.left}>
				<img src={seguridad} width="200px" alt="User Icon"/>
				
				
					<Link to="/login">
						<button type="button" className={styles.white_btn}>
							Return
						</button>
					</Link>
				</div>
				
				<div className={styles.right}>

				<h1>Confirm password </h1>

				
				
					
					<form className={styles.form_container} >
						<input
                        	type="password"
							placeholder="Password"
							name="password"
							onSubmit={handleSubmit(onSubmit)}
							value={data.password1}
							required
							className={styles.input}
						/>
                        <input
                        	type="password"
							placeholder="Password"
							name="password"
							onSubmit={handleSubmit(onSubmit)}
							value={data.password}
							required
							className={styles.input}
						/>
						<button type="submit" onClick={()=>{values();}} className={styles.green_btn}>
							Send
						</button>
                     
					</form>
				</div>
			</div>
		</div>
	);

}

export default Email;
