import styles from "./styles.module.css";
import { useState } from "react";
import { useForm} from "react-hook-form";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

import seguridad from "../../assets/img/pass.png";
import Swal from "sweetalert2";

function Email () {

const querystring = window.location.search
const params  = new URLSearchParams(querystring)

    const data = useState({
		password: "",
	
	});

	const url = 'http://localhost:3000/api/user/update_password'
	const navigator = useNavigate()
	const{handleSubmit, register, formState: {errors}} = useForm();
	
	const onSubmit = values =>{
	var myHeaders = new Headers()
	myHeaders.append('Content-Type', 'application/json')

	var raw = JSON.stringify ({
		"email": params.get('email'),
	});

	if(values.password1 == values.password) {
		const data = values
		axios.put(url,{
			email: params.get('email'),
			password: data.password
		})
		.then(response => {
		console.log(response.data);
		Swal.fire(
			''+response.data.err+'!',
			'',
			'success'
		)
		})
		navigator('/login')
	}
	else {
		Swal.fire(
            'Password Error',
			'Valida que sean igual',
            'error',
        )
		}
}



    return (
		<div className={styles.signup_container}>
			<div className={styles.signup_form_container}>
				<div className={styles.left}>
				<img src={seguridad} width="200px" alt="User Icon"/>
				
				
					<Link to="/login">
						<button type="button" className={styles.white_btn}>
							Return
						</button>
					</Link>
				</div>
				
				<div className={styles.right}>

				<h1>Confirm password </h1>

				
					
					<form className ="was-validated" noValidate onSubmit={handleSubmit(onSubmit)}> 
						<input
                        	type="password"
							className="form-control"
							placeholder="Password" required {...register("password",{
							required:{
								value: true,
								message: "El campo requerido",
							
							},
							minLength:{
								value: 8,
								message: "La contraseña debe tener minimo 8 caracteres",
							}

						})}></input>
                        <input
                        	type="password"
							className="form-control"
							placeholder="Password1" required {...register("password1",{
							required:{
								value: true,
								message: "El campo requerido",
							
							},
							minLength:{
								value: 8,
								message: "La contraseña debe tener minimo 8 caracteres",
							}

						})}></input>
						{errors.password&& <span className="text-danger">{errors.password.message}</span>}
						<div className="d-grid">
							<button type="submit" className="btn btn-primary">Restablecer</button>
						</div>
						
					</form>
				</div>
			</div>
		</div>
	);
}

export default Email;
