import styles from "./styles.module.css";
import { useState } from "react";
import { useForm} from "react-hook-form";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

import seguridad from "../../assets/img/pass.png";
import Swal from "sweetalert2";

function Email () {


    return (
		<div className={styles.signup_container}>
			<div className={styles.signup_form_container}>
				<div className={styles.left}>
				<img src={seguridad} width="200px" alt="User Icon"/>
				
				
					<Link to="/login">
						<button type="button" className={styles.white_btn}>
							Return
						</button>
					</Link>
				</div>
				
				<div className={styles.right}>

				<h1>Confirm password </h1>

				
					
					<form className ="was-validated" noValidate onSubmit={handleSubmit(onSubmit)}> 
						<input
                        	type="password"
							className="form-control"
							placeholder="Password" required {...register("password",{
							required:{
								value: true,
								message: "El campo requerido",
							
							},
							minLength:{
								value: 8,
								message: "La contrase単a debe tener minimo 8 caracteres",
							}

						})}></input>
                        <input
                        	type="password"
							className="form-control"
							placeholder="Password1" required {...register("password1",{
							required:{
								value: true,
								message: "El campo requerido",
							
							},
							minLength:{
								value: 8,
								message: "La contrase単a debe tener minimo 8 caracteres",
							}

						})}></input>
						{errors.password&& <span className="text-danger">{errors.password.message}</span>}
						<div className="d-grid">
							<button type="submit" className="btn btn-primary">Restablecer</button>
						</div>
						
					</form>
				</div>
			</div>
		</div>
	);
}

export default Email;
