import { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import styles from "./styles.module.css";
import crear from "../../assets/img/cuenta.png";
import {AiOutlineEye, AiOutlineEyeInvisible} from "react-icons/ai";

const Signup = () => {
	const [data, setData] = useState({
		name: "",
		email: "",
		password: "",
	});
	const [error, setError] = useState("");
	const navigate = useNavigate();
	const [state, setstate] = useState(false);

	const toggleBtn = () =>{
		setstate(prevState => !prevState);
	}


	const handleChange = ({ currentTarget: input }) => {
		setData({ ...data, [input.name]: input.value });
	};

	const handleSubmit = async (e) => {
		console.log(data.firstName, data.lastName, data.email, data.password)
		e.preventDefault();
		try {
			const url = "http://localhost:3000/api/user/create";
			const { data: res } = await axios.post(url, data);
			navigate("/login");
			console.log(data);
		} catch (error) {
			if (
				error.response &&
				error.response.status >= 400 &&
				error.response.status <= 500
			) {
				setError(error.response.data.message);
			}
		}
	};


	return (
		<div className={styles.signup_container}>
			<div className={styles.signup_form_container}>
				<div className={styles.left}>


					<Link to="/login">
						<button type="button" className={styles.white_btn}>
							Return
						</button>
					</Link>
				</div>
				<div className={styles.right}>
					<form className={styles.form_container} onSubmit={handleSubmit}>
						<h1>Create Account</h1>
						<img src={crear} width="200px" alt="User Icon"/>
						<input
							type="text"
							placeholder="Name"
							name="Name"
							onChange={handleChange}
							value={data.name}
							required
							className={styles.input}
						/>
						<input
							type="email"
							placeholder="Email"
							name="email"
							onChange={handleChange}
							value={data.email}
							required
							className={styles.input}
						/>
						<div className={styles.password}>
						<input
							type={state ? "text": "password" }
							placeholder="Password"
							name="password"
							onChange={handleChange}
							value={data.password}
							required
							className={styles.input}
						/> 	
							<button type="button"  className={styles.btn} onClick={toggleBtn}>
								{
									state ? <AiOutlineEyeInvisible/> : 
									<AiOutlineEye/>
								}
							</button>




						</div>


						{error && <div className={styles.error_msg}>{error}</div>}
						<button type="submit" className={styles.green_btn}>
							Sing Up
						</button>

					</form>
				</div>
			</div>
		</div>
	);
};

export default Signup;
